import React, { useState } from 'react';

const AgeCalculator = () => {
  const [dob, setDob] = useState('');
  const [age, setAge] = useState(null);

  const calculateAge = () => {
    const birthDate = new Date(dob);
    const today = new Date();
    let years = today.getFullYear() - birthDate.getFullYear();
    const monthDiff = today.getMonth() - birthDate.getMonth();

    if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
      years--;
    }

    setAge(years);
  };

  return (
    <div className="p-6">
      <h2 className="text-xl font-semibold mb-4">Age Calculator</h2>
      <input
        type="date"
        value={dob}
        onChange={(e) => setDob(e.target.value)}
        className="border px-3 py-2 rounded mr-4"
      />
      <button onClick={calculateAge} className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">
        Calculate Age
      </button>
      {age !== null && <p className="mt-4 text-lg">You are {age} years old.</p>}
    </div>
  );
};

export default AgeCalculator;
